    Formio.projectUrlSet = true;
  }

  static getAppUrl() {
    console.warn('Formio.getAppUrl() is deprecated. Use Formio.getProjectUrl instead.');
    return Formio.projectUrl;
  }

  static getProjectUrl() {
    return Formio.projectUrl;
  }

  static clearCache() {
    Formio.cache = {};
  }

  static noop() {}
  static identity(value) {
    return value;
  }

  static deregisterPlugin(plugin) {
    const beforeLength = Formio.plugins.length;
    Formio.plugins = Formio.plugins.filter((p) => {
      if (p !== plugin && p.__name !== plugin) {
        return true;
      }

      (p.deregister || Formio.noop).call(plugin, Formio);
      return false;
    });
    return beforeLength !== Formio.plugins.length;
  }

  static registerPlugin(plugin, name) {
    Formio.plugins.push(plugin);
    Formio.plugins.sort((a, b) => (b.priority || 0) - (a.priority || 0));
    plugin.__name = name;
    (plugin.init || Formio.noop).call(plugin, Formio);
  }

  static getPlugin(name) {
    for (const plugin of Formio.plugins) {
      if (plugin.__name === name) {
        return plugin;
      }
    }

    return null;
  }

  static pluginWait(pluginFn, ...args) {
    return Promise.all(Formio.plugins.map((plugin) =>
      (plugin[pluginFn] || Formio.noop).call(plugin, ...args)));
  }

  static pluginGet(pluginFn, ...args) {
    const callPlugin = (index) => {
      const plugin = Formio.plugins[index];

      if (!plugin) {
        return Promise.resolve(null);
      }

      return Promise.resolve((plugin[pluginFn] || Formio.noop).call(plugin, ...args))
        .then((result) => {
          if (!isNil(result)) {
            return result;
          }

          return callPlugin(index + 1);
        });
    };
    return callPlugin(0);
  }

  static pluginAlter(pluginFn, value, ...args) {
    return Formio.plugins.reduce((value, plugin) =>
      (plugin[pluginFn] || Formio.identity)(value, ...args), value);
  }

  static accessInfo(formio) {
    const projectUrl = formio ? formio.projectUrl : Formio.projectUrl;
    return Formio.makeRequest(formio, 'accessInfo', `${projectUrl}/access`);
  }

  static currentUser(formio, options) {
    let projectUrl = formio ? formio.projectUrl : (Formio.projectUrl || Formio.baseUrl);
    projectUrl += '/current';
    const user = this.getUser(options);
    if (user) {
      return Formio.pluginAlter('wrapStaticRequestPromise', Promise.resolve(user), {
        url: projectUrl,
        method: 'GET',
        options
      });
    }

    const token = Formio.getToken(options);
    if ((!options || !options.external) && !token) {
      return Formio.pluginAlter('wrapStaticRequestPromise', Promise.resolve(null), {
        url: projectUrl,
        method: 'GET',
        options
      });
    }
    return Formio.makeRequest(formio, 'currentUser', projectUrl, 'GET', null, options)
      .then((response) => {
        Formio.setUser(response, options);
        return response;
      });
  }

  static logout(formio, options) {
    options = options || {};
    options.formio = formio;
    Formio.setToken(null, options);
    Formio.setUser(null, options);
    Formio.clearCache();
    const projectUrl = formio ? formio.projectUrl : Formio.baseUrl;
    return Formio.makeRequest(formio, 'logout', `${projectUrl}/logout`);
  }

  static oAuthCurrentUser(formio, token) {
    return Formio.currentUser(formio, {
      external: true,
      headers: {
        Authorization: `Bearer ${token}`
      }
    });
  }

  static oktaInit(options) {
    options = options || {};
    if (typeof OktaAuth !== undefined) {
      options.OktaAuth = OktaAuth;
    }

    if (typeof options.OktaAuth === undefined) {
      const errorMessage = 'Cannot find OktaAuth. Please include the Okta JavaScript SDK within your application. See https://developer.okta.com/code/javascript/okta_auth_sdk for an example.';
      console.warn(errorMessage);
      return Promise.reject(errorMessage);
    }
    return new Promise((resolve, reject) => {
      const Okta = options.OktaAuth;
      delete options.OktaAuth;
      var authClient = new Okta(options);
      var accessToken = authClient.tokenManager.get('accessToken');
      if (accessToken) {
        resolve(Formio.oAuthCurrentUser(options.formio, accessToken.accessToken));
      }
      else if (location.hash) {
        authClient.token.parseFromUrl()
          .then(token => {
            authClient.tokenManager.add('accessToken', token);
            resolve(Formio.oAuthCurrentUser(options.formio, token.accessToken));
          })
          .catch(err => {
            console.warn(err);
            reject(err);
          });
      }
      else {
        authClient.token.getWithRedirect({
          responseType: 'token',
          scopes: options.scopes
        });
        resolve(false);
      }
    });
  }

  static ssoInit(type, options) {
    switch (type) {
      case 'okta':
        return Formio.oktaInit(options);
      default:
        console.warn('Unknown SSO type');
        return Promise.reject('Unknown SSO type');
    }
  }

  static requireLibrary(name, property, src, polling) {
    if (!Formio.libraries.hasOwnProperty(name)) {
      Formio.libraries[name] = {};
      Formio.libraries[name].ready = new Promise((resolve, reject) => {
        Formio.libraries[name].resolve = resolve;
        Formio.libraries[name].reject = reject;
      });

      const callbackName = `${name}Callback`;

      if (!polling && !window[callbackName]) {
        window[callbackName] = () => Formio.libraries[name].resolve();
      }

      // See if the plugin already exists.
      const plugin = _get(window, property);
      if (plugin) {
        Formio.libraries[name].resolve(plugin);
      }
      else {
        src = Array.isArray(src) ? src : [src];
        src.forEach((lib) => {
          let attrs = {};
          let elementType = '';
          if (typeof lib === 'string') {
            lib = {
              type: 'script',
              src: lib
            };
          }
          switch (lib.type) {
            case 'script':
              elementType = 'script';
              attrs = {
                src: lib.src,
                type: 'text/javascript',
                defer: true,
                async: true
              };
              break;
            case 'styles':
              elementType = 'link';
              attrs = {
                href: lib.src,
                rel: 'stylesheet'
              };
              break;
          }

          // Add the script to the top page.
          const script = document.createElement(elementType);
          for (const attr in attrs) {
            script.setAttribute(attr, attrs[attr]);
          }
          document.getElementsByTagName('head')[0].appendChild(script);
        });

        // if no callback is provided, then check periodically for the script.
        if (polling) {
          const interval = setInterval(() => {
            const plugin = _get(window, property);
            if (plugin) {
              clearInterval(interval);
              Formio.libraries[name].resolve(plugin);
            }
          }, 200);
        }
      }
    }
    return Formio.libraries[name].ready;
  }

  static libraryReady(name) {
    if (
      Formio.libraries.hasOwnProperty(name) &&
      Formio.libraries[name].ready
    ) {
      return Formio.libraries[name].ready;
    }

    return Promise.reject(`${name} library was not required.`);
  }
}

// Define all the static properties.
Formio.libraries = {};
Formio.Promise = Promise;
Formio.Headers = Headers;
Formio.baseUrl = 'https://api.form.io';
Formio.projectUrl = Formio.baseUrl;
Formio.projectUrlSet = false;
Formio.plugins = [];
Formio.cache = {};
Formio.providers = providers;
Formio.events = new EventEmitter({
  wildcard: false,
  maxListeners: 0
});

if (typeof global === 'object' && !global.Formio) {
  global.Formio = Formio;
}

import Formio from './Formio';
import Webform from './Webform';

export default class PDF extends Webform {
  constructor(element, options) {
    super(element, options);

    // Resolve when the iframe is ready.
    this.iframeReady = new Promise((resolve) => (this.iframeReadyResolve = resolve));
  }

  postMessage(message) {
    this.iframeReady.then(() => {
      if (this.iframe && this.iframe.contentWindow) {
        this.iframe.contentWindow.postMessage(JSON.stringify(message), '*');
      }
    });
  }

  // Do not clear the iframe.
  clear() {}

  redraw() {
    this.postMessage({ name: 'redraw' });
  }

  getSrc() {
    if (!this._form || !this._form.settings || !this._form.settings.pdf) {
      return '';
    }
    let iframeSrc = `${this._form.settings.pdf.src}.html`;
    const params = [`id=${this.id}`];
    if (this.options.readOnly) {
      params.push('readonly=1');
    }
    if (this.options.zoom) {
      params.push(`zoom=${this.options.zoom}`);
    }
    if (this.options.builder) {
      params.push('builder=1');
    }
    if (params.length) {
      iframeSrc += `?${params.join('&')}`;
    }
    return iframeSrc;
  }

  setForm(form) {
    return super.setForm(form).then(() => {
      if (this.formio) {
        form.projectUrl = this.formio.projectUrl;
        form.url = this.formio.formUrl;
        form.base = this.formio.base;
      }
      this.postMessage({ name: 'form', data: form });
    });
  }

  setSubmission(submission) {
    submission.readOnly = !!this.options.readOnly;
    this.postMessage({ name: 'submission', data: submission });
    return super.setSubmission(submission).then(() => {
      if (this.formio) {
        this.formio.getDownloadUrl().then((url) => {
          // Add a download button if it has a download url.
          if (!url) {
            return;
          }
          if (!this.downloadButton) {
            if (this.options.primaryProject) {
              url += `&project=${this.options.primaryProject}`;
            }
            this.downloadButton = this.ce('a', {
              href: url,
              target: '_blank',
              style: 'position:absolute;right:10px;top:110px;cursor:pointer;'
            }, this.ce('img', {
              src: require('./pdf.image'),
              style: 'width:3em;'
            }));
            this.element.insertBefore(this.downloadButton, this.iframe);
          }
        });
      }
    });
  }

  addComponent(component, element, data, before) {
    // Never add the component to the DOM.
    super.addComponent(component, element, data, before, true);
  }

  // Iframe should always be shown.
  showElement() {}
  build() {
    // Do not rebuild the iframe...
    if (this.iframe) {
      this.addComponents();
      return;
    }

    this.zoomIn = this.ce('span', {
      style: 'position:absolute;right:10px;top:10px;cursor:pointer;',
      class: 'btn btn-default btn-secondary no-disable'
    }, this.ce('i', {
      class: this.iconClass('zoom-in')
    }));
    this.addEventListener(this.zoomIn, 'click', (event) => {
      event.preventDefault();
      this.postMessage({ name: 'zoomIn' });
    });

    this.zoomOut = this.ce('span', {
      style: 'position:absolute;right:10px;top:60px;cursor:pointer;',
      class: 'btn btn-default btn-secondary no-disable'
    }, this.ce('i', {
      class: this.iconClass('zoom-out')
    }));
    this.addEventListener(this.zoomOut, 'click', (event) => {
      event.preventDefault();
      this.postMessage({ name: 'zoomOut' });
    });

    this.iframe = this.ce('iframe', {
      src: this.getSrc(),
      id: `iframe-${this.id}`,
      seamless: true,
      class: 'formio-iframe'
    });

    // Handle an iframe submission.
    this.on('iframe-submission', (submission) => {
      this.setSubmission(submission).then(() => this.submit());
    });

    // Trigger when this form is ready.
    this.on('iframe-ready', () => this.iframeReadyResolve());

    this.appendChild(this.element, [
      this.zoomIn,
      this.zoomOut,
      this.iframe
    ]);

    if (!this.options.readOnly) {
      this.submitButton = this.ce('button', {
        type: 'button',
        class: 'btn btn-primary'
      }, 'Submit');

      this.addEventListener(this.submitButton, 'click', () => {
        this.postMessage({ name: 'getSubmission' });
      });
      this.appendChild(this.element, this.submitButton);
    }

    this.addComponents();
  }
}

/**
 * Listen for window messages.
 */
window.addEventListener('message', (event) => {
  let eventData = null;
  try {
    eventData = JSON.parse(event.data);
  }
  catch (err) {
    eventData = null;
  }

  // If this form exists, then emit the event within this form.
  if (
    eventData &&
    eventData.name &&
    eventData.formId &&
    Formio.forms.hasOwnProperty(eventData.formId)
  ) {
    Formio.forms[eventData.formId].emit(`iframe-${eventData.name}`, eventData.data);
  }
});

import _ from 'lodash';

import WebformBuilder from './WebformBuilder';
import { getElementRect } from './utils/utils';
import PDF from './PDF';

export default class PDFBuilder extends WebformBuilder {
  get defaultComponents() {
    return {
      pdf: {
        title: 'PDF Fields',
        weight: 0,
        default: true,
        components: {
          textfield: true,
          number: true,
          password: true,
          email: true,
          phoneNumber: true,
          currency: true,
          checkbox: true,
          signature: true,
          select: true,
          textarea: true,
          datetime: true
        }
      },
      basic: false,
      advanced: false,
      layout: false,
      data: false,
      premium: false,
      resource: false
    };
  }

  addDropZone() {
    if (!this.dropZone) {
      this.dropZone = this.ce('div', {
        class: 'formio-drop-zone'
      });
      this.prepend(this.dropZone);
    }
    this.addEventListener(this.dropZone, 'dragover', (event) => {
      event.preventDefault();
      return false;
    });
    this.addEventListener(this.dropZone, 'drop', (event) => {
      event.preventDefault();
      this.dragStop(event);
      return false;
    });
    this.disableDropZone();
  }

  render() {
    return this.onElement.then(() => {
      this.clear();
      this.build();
      this.isBuilt = true;
      this.on('resetForm', () => this.resetValue());
      this.on('refreshData', () => this.updateValue());
      setTimeout(() => {
        this.onChange();
        this.emit('render');
      }, 1);
    });
  }

  enableDropZone() {
    if (this.dropZone) {
      const iframeRect = getElementRect(this.pdfForm.element);
      this.dropZone.style.height = iframeRect && iframeRect.height  ? `${iframeRect.height}px` : '1000px';
      this.dropZone.style.width = iframeRect && iframeRect.width ? `${iframeRect.width}px` : '100%';
      this.addClass(this.dropZone, 'enabled');
    }
  }

  disableDropZone() {
    if (this.dropZone) {
      this.removeClass(this.dropZone, 'enabled');
    }
  }

  addComponentTo(parent, schema, element, sibling) {
    const comp = super.addComponentTo(parent, schema, element, sibling);
    comp.isNew = true;
    if (this.pdfForm && schema.overlay) {
      this.pdfForm.postMessage({ name: 'addElement', data: schema });
    }
    return comp;
  }

  addComponent(component, element, data, before) {
    return super.addComponent(component, element, data, before, true);
  }

  deleteComponent(component) {
    if (this.pdfForm && component.component) {
      this.pdfForm.postMessage({ name: 'removeElement', data: component.component });
    }
    return super.deleteComponent(component);
  }

  dragStart(event, component) {
    event.stopPropagation();
    event.dataTransfer.setData('text/plain', 'true');
    this.currentComponent = component;
    this.enableDropZone();
  }

  removeEventListeners(all) {
    super.removeEventListeners(all);
    _.each(this.groups, (group) => {
      _.each(group.components, (builderComponent) => {
        this.removeEventListener(builderComponent, 'dragstart');
        this.removeEventListener(builderComponent, 'dragend');
      });
    });
  }

  clear() {
    this.destroy();
  }
  redraw() {
    if (this.pdfForm) {
      this.pdfForm.postMessage({ name: 'redraw' });
    }
  }

  dragStop(event) {
    const schema = this.currentComponent ? this.currentComponent.schema : null;
    if (!schema) {
      return false;
    }

    schema.overlay = {
      top: event.offsetY,
      left: event.offsetX,
      width: 100,
      height: 20
    };

    this.addComponentTo(this, schema, this.getContainer());
    this.disableDropZone();
    return false;
  }

  // Don't need to add a submit button here... the pdfForm will already do this.
  addSubmitButton() {}

  addBuilderComponent(component, group) {
    const builderComponent = super.addBuilderComponent(component, group);
    if (builderComponent) {
      builderComponent.element.draggable = true;
      builderComponent.element.setAttribute('draggable', true);
      this.addEventListener(builderComponent.element, 'dragstart', (event) => this.dragStart(event, component), true);
      this.addEventListener(builderComponent.element, 'dragend', () => this.disableDropZone(), true);
    }
    return builderComponent;
  }

  refreshDraggable() {
    this.addSubmitButton();
    this.builderReadyResolve();
  }

  build() {
    this.buildSidebar();
    if (!this.pdfForm) {
      this.element.noDrop = true;
      this.pdfForm = new PDF(this.element, this.options);
      this.addClass(this.pdfForm.element, 'formio-pdf-builder');
    }
    this.pdfForm.destroy();
    this.pdfForm.on('iframe-elementUpdate', schema => {
      const component = this.getComponentById(schema.id);
      if (component && component.component) {
        component.component.overlay = {
          page: schema.page,
          left: schema.left,
          top: schema.top,
          height: schema.height,
          width: schema.width
        };
        this.editComponent(component);
        this.emit('updateComponent', component);
      }
      return component;
    });
    this.pdfForm.on('iframe-componentUpdate', schema => {
      const component = this.getComponentById(schema.id);
      if (component && component.component) {
        component.component.overlay = {
          page: schema.overlay.page,
          left: schema.overlay.left,
          top: schema.overlay.top,
          height: schema.overlay.height,
          width: schema.overlay.width
        };
        this.emit('updateComponent', component);
      }
      return component;
    });
    this.pdfForm.on('iframe-componentClick', schema => {
      const component = this.getComponentById(schema.id);
      if (component) {
        this.editComponent(component);
      }
    });
    this.addComponents();
    this.addDropZone();
    this.updateDraggable();
    this.formReadyResolve();
  }

  setForm(form) {
    return super.setForm(form).then(() => {
      return this.ready.then(() => {
        if (this.pdfForm) {
          this.pdfForm.postMessage({ name: 'form', data: form });
          return this.pdfForm.setForm(form);
        }
        return form;
      });
    });
  }
}

import _ from 'lodash';
import moment from 'moment';
import EventEmitter from 'eventemitter2';
import i18next from 'i18next';
import Formio from './Formio';
