        paymentRequest.canMakePayment().then((result) => {
          if (result) {
            // Display label separator
            this.stripeSeparator.style.display = 'block';
            paymentRequestElement.mount(this.stripeElementPayButton);
          }
        });
      }
    });
  }
}

