import _trim from 'lodash/trim';
const trim = function(text) {
  return _trim(text, '/');
};
const path = function(items) {
  return items.filter(item => !!item).map(trim).join('/');
};
const s3 = (formio) => ({
  uploadFile(file, fileName, dir, progressCallback) {
    return new Promise(((resolve, reject) => {
      // Send the pre response to sign the upload.
      const pre = new XMLHttpRequest();

      // This only fires on a network error.
      pre.onerror = (err) => {
        err.networkError = true;
        reject(err);
      };

      pre.onabort = reject;

      pre.onload = () => {
        if (pre.status >= 200 && pre.status < 300) {
          const response = JSON.parse(pre.response);

          // Send the file with data.
          const xhr = new XMLHttpRequest();

          if (typeof progressCallback === 'function') {
            xhr.upload.onprogress = progressCallback;
          }

          response.data.fileName = fileName;
          response.data.key = path([response.data.key, dir, fileName]);

          // Fire on network error.
          xhr.onerror = (err) => {
            err.networkError = true;
            reject(err);
          };

          xhr.onload = () => {
            if (xhr.status >= 200 && xhr.status < 300) {
              resolve({
                storage: 's3',
                name: fileName,
                bucket: response.bucket,
                key: response.data.key,
                url: path([response.url, response.data.key]),
                acl: response.data.acl,
                size: file.size,
                type: file.type
              });
            }
            else {
              reject(xhr.response || 'Unable to upload file');
            }
          };

          xhr.onabort = reject;
          if (response.signed) {
            xhr.open('PUT', response.signed);
            xhr.setRequestHeader('Content-Type', file.type);
            xhr.send(file);
          }
          else {
            const fd = new FormData();
            for (const key in response.data) {
              fd.append(key, response.data[key]);
            }
            fd.append('file', file);
            xhr.open('POST', response.url);
            xhr.send(fd);
          }
        }
        else {
          reject(pre.response || 'Unable to sign file');
        }
      };

      pre.open('POST', `${formio.formUrl}/storage/s3`);

      pre.setRequestHeader('Accept', 'application/json');
      pre.setRequestHeader('Content-Type', 'application/json; charset=UTF-8');
      const token = formio.getToken();
      if (token) {
        pre.setRequestHeader('x-jwt-token', token);
      }

      pre.send(JSON.stringify({
        name: path([dir, fileName]),
        size: file.size,
        type: file.type
      }));
    }));
  },
  downloadFile(file) {
    if (file.acl !== 'public-read') {
      return formio.makeRequest('file', `${formio.formUrl}/storage/s3?bucket=${trim(file.bucket)}&key=${trim(file.key)}`, 'GET');
    }
    else {
      return Promise.resolve(file);
    }
  }
});

s3.title = 'S3';
export default s3;

const url = (formio) => ({
  title: 'Url',
  name: 'url',
  uploadFile(file, fileName, dir, progressCallback, url) {
    return new Promise(((resolve, reject) => {
      const data = {
        dir: dir,
        name: fileName,
        file: file
      };

      // Send the file with data.
      const xhr = new XMLHttpRequest();

      if (typeof progressCallback === 'function') {
        xhr.upload.onprogress = progressCallback;
      }

      const fd = new FormData();
      for (const key in data) {
        fd.append(key, data[key]);
      }

      xhr.onload = () => {
        if (xhr.status >= 200 && xhr.status < 300) {
          // Need to test if xhr.response is decoded or not.
          let respData = {};
          try {
            respData = (typeof xhr.response === 'string') ? JSON.parse(xhr.response) : {};
            respData = (respData && respData.data) ? respData.data : respData;
          }
          catch (err) {
            respData = {};
          }

          const url = respData.hasOwnProperty('url') ? respData.url : `${xhr.responseURL}/${fileName}`;
          resolve({
            storage: 'url',
            name: fileName,
            url,
            size: file.size,
            type: file.type,
            data: respData
          });
        }
        else {
          reject(xhr.response || 'Unable to upload file');
        }
      };

      // Fire on network error.
      xhr.onerror = () => reject(xhr);

      xhr.onabort = () => reject(xhr);

      xhr.open('POST', url);
      const token = formio.getToken();
      if (token) {
        xhr.setRequestHeader('x-jwt-token', token);
      }
      xhr.send(fd);
    }));
  },
  downloadFile(file) {
    // Return the original as there is nothing to do.
    return Promise.resolve(file);
  }
});

