export default {
  transform: (type, text) => {
    const columns = {
      '1': 'one',
      '2': 'two',
      '3': 'three',
      '4': 'four',
      '5': 'five',
      '6': 'six',
      '7': 'seven',
      '8': 'eight',
      '9': 'nine',
      '10': 'ten',
      '11': 'eleven',
      '12': 'twelve',
      '13': 'thirteen',
      '14': 'fourteen',
      '15': 'fifteen',
      '16': 'sixteen'
    };
    const classes = {
      'has-error': 'error',
      'is-invalid': 'error',
    };
    switch (type) {
      case 'columns':
        return columns.hasOwnProperty(text.toString()) ? columns[text.toString()] : text;
      case 'class':
        return classes.hasOwnProperty(text.toString()) ? classes[text.toString()] : text;
    }
    return text;
  },
  defaultIconset: 'icon',
  iconClass,
  builder,
  builderComponent,
  builderComponents,
  builderEditForm,
  builderPlaceholder,
  builderSidebar,
  button,
  checkbox,
  columns,
  component,
